import type { TaskModel, UserModel } from '$/api/@types/models';
import { S3_PREFIX } from '$/domain/repository/s3Repo';
import type { MultipartFile } from '@fastify/multipart';
import { randomUUID } from 'crypto';

const dataToUrl = (data: MultipartFile) => {
  const s3Key = `tasks/images/${randomUUID()}.${data.filename.split('.').at(-1)}`;

  return { url: `${S3_PREFIX}${s3Key}`, s3Key };
};

export type DeletableTaskId = { type: 'DeletableTask'; val: string };

export const taskModel = {
  create: (user: UserModel, label: string, data: MultipartFile | undefined): TaskModel => ({
    id: randomUUID(),
    done: false,
    label,
    image: data === undefined ? undefined : dataToUrl(data),
    createdTime: Date.now(),
    author: { userId: user.id, name: user.name },
  }),
  deleteOrThrow: (user: UserModel, task: TaskModel): DeletableTaskId => {
    if (user.id !== task.author.userId) throw new Error('cannot delete');

    return { type: 'DeletableTask', val: task.id };
  },
  updateOrThrow: (
    user: UserModel,
    task: TaskModel,
    updateData: { done: boolean; label: string }
  ): TaskModel => {
    if (user.id !== task.author.userId) throw new Error('cannot update task');

    return { ...task, ...updateData };
  },
};

import type { TaskModel } from '$/api/@types/models';
import type { DeletableTaskId } from '$/domain/model/taskModel';
import type { Prisma, Task, User } from '@prisma/client';
import { S3_PREFIX } from './s3Repo';

const toModel = (task: Task & { User: User }): TaskModel => ({
  id: task.id,
  label: task.label,
  done: task.done,
  createdTime: task.createdAt.getTime(),
  image:
    task.imageKey === null
      ? undefined
      : { url: `${S3_PREFIX}${task.imageKey}`, s3Key: task.imageKey },
  author: { userId: task.userId, name: task.User.name },
});

export const taskRepo = {
  save: async (tx: Prisma.TransactionClient, task: TaskModel) => {
    await tx.task.upsert({
      where: { id: task.id },
      update: { done: task.done, label: task.label, imageKey: task.image?.s3Key },
      create: {
        id: task.id,
        userId: task.author.userId,
        done: task.done,
        label: task.label,
        imageKey: task.image?.s3Key,
        createdAt: new Date(task.createdTime),
      },
    });
  },
  delete: async (tx: Prisma.TransactionClient, deletableTaskId: DeletableTaskId) => {
    await tx.task.delete({ where: { id: deletableTaskId.val } });
  },
  findAll: (tx: Prisma.TransactionClient, limit?: number): Promise<TaskModel[]> =>
    tx.task
      .findMany({ take: limit, include: { User: true }, orderBy: { createdAt: 'desc' } })
      .then((tasks) => tasks.map(toModel)),
  findByIdOrThrow: (tx: Prisma.TransactionClient, taskId: string): Promise<TaskModel> =>
    tx.task.findUniqueOrThrow({ where: { id: taskId }, include: { User: true } }).then(toModel),
};

import type { TaskModel, UserModel } from '$/api/@types/models';
import { taskModel } from '$/domain/model/taskModel';
import { s3Repo } from '$/domain/repository/s3Repo';
import { taskRepo } from '$/domain/repository/taskRepo';
import { transaction } from '$/service/prismaClient';
import type { MultipartFile } from '@fastify/multipart';

export const taskUseCase = {
  create: (user: UserModel, label: string, image: MultipartFile | undefined) =>
    transaction<TaskModel>('RepeatableRead', async (tx) => {
      const task = taskModel.create(user, label, image);

      if (image !== undefined && task.image !== undefined) {
        await s3Repo.save(task.image.s3Key, image);
      }

      await taskRepo.save(tx, task);

      return task;
    }),
  delete: (user: UserModel, taskId: string) =>
    transaction('RepeatableRead', async (tx) => {
      const task = await taskRepo.findByIdOrThrow(tx, taskId);
      const deletableTaskId = taskModel.deleteOrThrow(user, task);

      await taskRepo.delete(tx, deletableTaskId);
    }),
  update: (user: UserModel, taskId: string, done: boolean, label: string) =>
    transaction<TaskModel>('RepeatableRead', async (tx) => {
      const task = await taskRepo.findByIdOrThrow(tx, taskId);
      const newTask = taskModel.updateOrThrow(user, task, { done, label });

      await taskRepo.save(tx, newTask);

      return newTask;
    }),
};

